com.cloudbees.plugins.flow.BuildFlow.dsl:
    build("0_cleanup-ruby-package-repository", release: "<%= flavor %>")
    build("0_cleanup-rock-package-repository", release: "<%= flavor %>")
<% if parallel # parallel builds%>
ignore(FAILURE){
parallel (
    <% flow[:gems].each_with_index do |gem,i| %>
    {
    build("<%= gem%>", release: "<%= flavor %>")}<%= gems.size == (i+1) ? "" : "," %>
    <% end %>
    )
}
//out.println 'Rock-Packages'
<% flow[:packages].each do |jobs| %>
ignore(FAILURE){
    parallel (
    <% jobs.each_with_index do |job,i| %>
    { build("<%= job %>", release: "<%=  flavor %>")}<%= jobs.size == (i+1) ? "" : "," %>
    <% end %>
    )
}
<% end %>

<% else # non-parallel builds%>
    <% flow[:gems].each_with_index do |gem,i| %>
retry(3) {
    build("0_cleanup-package", release: "<%= flavor %>", package: "<%= debian_ruby_name(gem)%>")
    build("0_cleanup-package", release: "<%= flavor %>", package: "<%= gem%>")
    build("<%= gem%>", release: "<%= flavor %>")
}
    <% end %>

    <% flow[:packages].each_with_index do |job,i| %>
retry(3) {
    build("0_cleanup-package", release: "<%= flavor %>", package: "<%= job%>")
    build("<%= job %>", release: "<%=  flavor %>")
}
    <% end %>
<% end # parallel builds%>
