com.cloudbees.plugins.flow.BuildFlow.dsl:
//build("unlock")
//Ruby-Gem-Packages
//rm <%= File.dirname ENV['AUTOPROJ_CURRENT_ROOT'] %>/${release}/rock-osdeps.osdeps
ignore(FAILURE){
<% if parallel # parallel builds%>
parallel (
    <% gems.each_with_index do |gem,i| %>
    {
    build("<%= gem%>", release: "<%= flavor %>")}<%= gems.size == (i+1) ? "" : "," %>
    <% end %>
    )
}
//out.println 'Rock-Packages'
<% flow.each do |jobs| %>
ignore(FAILURE){
    parallel (
    <% jobs.each_with_index do |job,i| %>
    { build("<%= job %>", release: "<%=  flavor %>")}<%= jobs.size == (i+1) ? "" : "," %>
    <% end %>
    )
}
<% end %>

<% else # non-parallel builds%>
    <% gems.each_with_index do |gem,i| %>
    build("<%= gem%>", release: "<%= flavor %>"),
    <% end %>

    <% flow = flow.flatten %>
    <% flow.each_with_index do |job,i| %>
    build("<%= job %>", release: "<%=  flavor %>")<%= flow.size == (i+1) ? "" : "," %>
    <% end %>
    }

<% end # parellel builds%>
